import React from 'react'
import '../index.css';
const Pizza = (props) => {

    const onClick = () => {
        props.removePizza(props.pizza.id)
    }
    return (
        <div>
<img className="size" src={require(`../images/size/${props.pizza.size}.png`)} id="pot">
<img className="sauce" src={require(`../images/sauce/${props.pizza.sauce}.png`)} id="ingredients">
<img className="cheese" src={require(`../images/cheese/${props.pizza.cheese}.png`)} id="ingredients">
{props.pizza.toppings.map((top) =>
<img className="toppings" src={require(`../images/toppings/${top}.png`)} id="ingredients">
</img>)}
{props.pizza.gourmet_toppings.map((top) =>
<img className="gourmetToppings" src={require(`../images/gourmet_toppings/${top}.png`)}  id="ingredients">

</img>)}




</img>
</img>
</img>

            <button onClick={onClick}>Remove</button>
        </div>
    )
}
export default Pizza
