        props.removePizza(props.pizza.id)
    }
    return (
        <div>
<div className="size" src={`../images/size/${props.pizza.size}.png`} id="pot">
<div className="sauce" src={`../images/sauce/${props.pizza.sauce}.png`} id="ingredients">
<div className="cheese" src={`../images/cheese/${props.pizza.cheese}.png`} id="ingredients">
{props.pizza.toppings.map((top) =>
<div className="toppings" id="ingredients">
</div>)}

<div className="gourmetToppings"  id="ingredients">

</div>




</div>
</div>
</div>
           // {props.pizza.size} pizza with {props.pizza.sauce} sauce, {props.pizza.cheese} cheese
            <button onClick={onClick}>Remove</button>
        </div>
    )
}
export default Pizza
