import React, { Fragment } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import '../App.css';

const cheeseArray = ['feta', 'parmesan', 'mozzarella', 'ricota']

const CheeseForm = (props) => {
  return (

    <Fragment>
      <h1><label className="text-light" htmlFor="cheese" align="center">Cheese</label></h1>

      <div className="row">
        <div className="size" id="pot">

          <div className="sauce" id="ingredients">
            <img id="ingredients" src={require(`../images/sauce/${props.pizzar.sauce}.png`)} />

            <div className="cheese" id="ingredients">
              <img id="ingredients" src={require(`../images/cheese/${props.pizzar.cheese}.png`)} />
              {props.pizzars.toppings.map((top) =>
                <div className="toppings" id="ingredients">
                  <img id="ingredients" src={require(`../images/toppings/${top}.png`)} />
                </div>)}
              {props.pizzars.gourmet_toppings.map((top) =>
                <div className="gourmetToppings" id="ingredients">
                  <img id="ingredients" src={require(`../images/gourmet_toppings/${top}.png`)} />
                </div>)}
            </div></div></div></div>
        <br></br> <br></br> <br></br> <br></br>

        {/* <p> */}
        <div className="row">
          {cheeseArray.map((cheese, index) => {
            return props.fillForm(cheese, 'cheese', index)
          })}
        </div>
        {/* </p> */}
        <br></br> <br></br> <br></br> <br></br>


    </Fragment>

      )
    }

export default CheeseForm
